```java
import com.azure.cosmos.*;

public class ChangeFeedProcessorExample {
    private static final String HOST = "https://<your-cosmos-account>.documents.azure.com:443/";
    private static final String MASTER_KEY = "<your-master-key>";
    private static final String DATABASE_ID = "CosmosDatabase";
    private static final String CONTAINER_ID = "ItemsContainer";
    private static final String LEASE_CONTAINER_ID = "LeaseContainer";

    public static void main(String[] args) {
        CosmosAsyncClient client = new CosmosClientBuilder()
                .endpoint(HOST)
                .key(MASTER_KEY)
                .buildAsyncClient();

        CosmosAsyncDatabase database = client.getDatabase(DATABASE_ID);
        CosmosAsyncContainer container = database.getContainer(CONTAINER_ID);
        CosmosAsyncContainer leaseContainer = database.getContainer(LEASE_CONTAINER_ID);

        ChangeFeedProcessor changeFeedProcessor = new ChangeFeedProcessorBuilder()
                .hostName("ChangeFeedProcessorHost")
                .feedContainer(container)
                .leaseContainer(leaseContainer)
                .handleChanges(docs -> {
                    for (CosmosItemProperties doc : docs) {
                        System.out.println("Получено съобщение: " + doc.toJson());
                    }
                })
                .buildChangeFeedProcessor();

        changeFeedProcessor.start()
                .subscribeOn(Schedulers.newThread())
                .subscribe(
                        () -> System.out.println("Change Feed Processor стартира."),
                        throwable -> System.err.println("Грешка: " + throwable.getMessage())
                );
    }
}
