```python
import random

class Character:
    def __init__(self name health mana attack defense spells):
        self.name = name
        self.health = health
        self.mana = mana
        self.attack = attack
        self.defense = defense
        self.spells = spells

    def is_alive(self):
        return self.health > 0

    def basic_attack(self opponent):
        if opponent.is_alive():
            damage = max(0 self.attack - opponent.defense)
            opponent.health -= damage
            return f"{self.name} attacks {opponent.name} for {damage} damage!"
        return f"{opponent.name} is already defeated!"

    def cast_spell(self spell opponent):
        if spell in self.spells and self.mana >= self.spells[spell]['cost']:
            self.mana -= self.spells[spell]['cost']
            damage = self.spells[spell]['damage']
            opponent.health -= damage
            return f"{self.name} casts {spell} on {opponent.name} dealing {damage} damage!"
        return f"{self.name} fails to cast {spell} due to lack of mana or availability!"

def multiplayer_battle(characters):
    turn_order = sorted(characters key=lambda x: random.random())
    while any(char.is_alive() for char in characters):
        for character in turn_order:
            if character.is_alive():
                # For simplicity in a real system get_player_action would be interactive
                action = get_player_action(character)
                target = choose_target(characters character)                
                if action == 'attack':
                    print(character.basic_attack(target))
                elif action == 'spell':
                    spell = choose_spell(character)
                    print(character.cast_spell(spell target))

def get_player_action(character):
    # This would normally be interactive
    return random.choice(['attack' 'spell'])

def choose_target(available_characters current_character):
    # Function to choose opponent this could be expanded to be more interactive
    return random.choice([char for char in available_characters if char is not current_character and char.is_alive()])

def choose_spell(character):
    return random.choice(list(character.spells.keys()))

# Example Setup
player = Character("Hero" 100 50 20 5 {"Fireball": {"damage": 30 "cost": 10}})
enemy = Character("Orc" 120 30 15 8 {"Lightning": {"damage": 25 "cost": 8}})
